func threeSum(nums []int) [][]int {
	var listSum [][]int
	mapList := make(map[int]int)
	var numMin, numMax, numStart, num1, num2, num3 int
	for _, num := range nums {
		mapList[num]++
		if num > numMax {
			numMax = num
		}
		if num < numMin {
			numMin = num
		}
	}
	numMin = -numMin
	if numMin < numMax {
		numStart = -(numMax * 2)
	} else {
		numStart = -numMin
	}
	for i := numStart; i <= 0; i++ {
		if mapList[i] > 0 {
			num1 = i
			for j := i; i <= j && j <= -i/2; j++ {
				if mapList[j] > 0 {
					num2 = j
					num3 = -(num1 + num2)
					if mapList[num3] > 0 {
						kol1 := 1
						kol2 := 1
						kol3 := 1
						if num1 == num2 {
							kol1++
							kol2--
							if num1 == num3 {
								kol1++
								kol3--
							}
						}
						if kol2 > 0 {
							if num2 == num3 {
								kol2++
								kol3--
							}
						}
						addOk := true
						if kol1 > 0 && mapList[num1] < kol1 {
							addOk = false
						}
						if kol2 > 0 && mapList[num2] < kol2 {
							addOk = false
						}
						if kol3 > 0 && mapList[num2] < kol3 {
							addOk = false
						}
						if addOk {
							elList := []int{num1, num2, num3}
							listSum = append(listSum, elList)
						}
					}

				}
			}
		}
	}

	return listSum
}
